function ok = testFeatureNormalize()    ok = true;    check = 1E-6;    tests = 0;        % create the test parameters    X = [ 1 ; 2 ; 3 ];    [Xn mu sigma] = featureNormalize( X );    tests = tests + 1;        if Xn == [ -1, 0 1 ] && mu == 2 && sigma == 1      fprintf('test %d results\n', tests);    else      ok = false;      fprintf('error!\n');    end          fprintf('mu: %d, sigma: %d\n', mu, sigma);    % tests complete...    if ok == true      fprintf('%d tests passed!\n', tests);    else      fprintf('tests failed...\n');    endend